void Manager::newChef(Chef c){
    ch[num]=c;
    num++;
}
void Manager::order(string dish){
    int flag=0;
    for(int i=0;i<num;i++){
        if(ch[i].isSignatureDish(dish)&&flag==0){
            ch[i].cook(dish);
            flag=1;
            break;
        }
    }
    if(flag==0)
        ch[0].cook(dish);
}
void Manager::order(string dish, int id){
    ch[id].cook(dish);
}
